import React, { useState } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "../auxiliary/Context"; 
import { setView, registerUser, logoutUser} from "../auxiliary/Action"; 
import { addUserToDB } from "../auxiliary/Action";
import '../extraStyles.css'

export default function RegisterPage() {

    const { state, dispatch } = React.useContext(StateContext);

    const [user, setUser] = useState({
        name: "",
        password: "",
        repeatPassword: ""
      });

    const [response, register] = useResource((username, password) => ({
      url: "/users",
      method: "post",
      data: { username, password },
    }));

    useEffect(() => {
      if (response && response.data) {
        dispatch(registerUser(response.data.username));
        dispatch(setView('login'));
      }
    }, [user, dispatch]);

    function handleChange(event) {
      const { name, value } = event.target;

      setUser(prevValue => {
        return {
          ...prevValue,
          [name]: value
        };
      });
    }

    function handleRegister(event) {
      event.preventDefault();

      // check whether password is match
      if (user.password !== user.repeatPassword) {
          window.alert("The passwords entered twice do not match. Please re-enter!");
          return;
      }

      // check whether user has existed
      const existingUser = mockDB.find(dbUser => dbUser.name === user.name);

      if(existingUser) {
          window.alert("The username already exists!");
          return;
      }

      // Send registration request to the backend
      register({ username: user.username, password: user.password }).then((response) => {
        if (response.data) {
            // Handle successful registration here
            alert("Sign up successfully!");
            dispatch(registerUser(user.username));
            // Navigate to the homepage or login page after successful registration
            dispatch(setView('login'));
        }
    }).catch((error) => {
        // Handle errors here, like user already exists
        if (error.response && error.response.status === 409) {
            alert("The username already exists!");
        } else {
            // Handle other types of errors (like network error, server error etc.)
            alert("An error occurred. Please try again later.");
        }
    });

      // dispatch(registerUser(user.name));

      //By creating a logoutUser action or a similar action, this action will clear the user status.
      dispatch(logoutUser());   
      dispatch(setView('login'));
    }

    return (
      <div className="loginPage-container">
        <form className="RegisterPage-form" onSubmit={handleRegister}>
          <div className="loginPage-input-container">
            <input
              className="loginPage-input"
              onChange={handleChange}
              name="name"
              value={user.name}
              placeholder="Username"
            />
            <input
              className="loginPage-input"
              onChange={handleChange}
              name="password"
              type="password"
              value={user.password}
              placeholder="Password"
            />
            <input
              className="loginPage-input"
              onChange={handleChange}
              name="repeatPassword"
              type="Password"
              value={user.repeatPassword}
              placeholder="Repeat Password"
            />
          </div>
          <div className="loginPage-button-container">
            <button 
                className="registerPage-submitBtn" 
                type="submit" 
                disabled={!user.name || !user.password}
            >
                Sign Up
            </button>
          </div>         
        </form>


      </div>
    );

}
