import React, { useState, useContext, useEffect } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "../auxiliary/Context"; 

function CreateArea(props) {

  const { state } = useContext(StateContext);
  const { user } = state;

  const [note, setNote] = useState({
    title: "",
    content: ""
  });

  const [post, createPost] = useResource(({ title, content, createdDate, isCompleted, completedDate, userEmail }) => ({
    url: "/posts",
    method: "post",
    data: { title, content, createdDate, isCompleted, completedDate, userEmail },
  }));

  // useEffect(() => {
  //   if (post && post.data) {
  //     console.log(post.data);
  //     props.onAdd(post.data);
  //   }
  // }, [post]);

  function handleChange(event) {
    const { name, value } = event.target;

    setNote(prevNote => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  async function submitNote(event) {
    event.preventDefault(); // 首先阻止表单的默认提交行为

    const newNote = {
      title: note.title,
      content: note.content,
      createdDate: new Date().toLocaleString(),
      isCompleted: false,
      completedDate: null,
      userEmail: user.name
    };

    try {
      const response = await createPost(newNote); // 等待后端响应
      if (response.data) {
        props.onAdd(response.data); // 使用后端提供的数据，包括ID
      }
      // 更新note状态以清空输入
      setNote({
        title: "",
        content: ""
      });
    } catch (error) {
      // 处理错误
      console.error('Failed to create post:', error);
    }
  }

  return (
    <div>
      <form>
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
        />
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows="3"
        />
        <button onClick={submitNote} disabled={!note.title}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
