import React, {useReducer, useState, useEffect } from "react";
import { useResource } from 'react-request-hook';
import { userAndViewReducer, notesReducer, mockDBReducer } from './auxiliary/Reducer';
import { StateContext } from './auxiliary/Context'
import { addNote, deleteNote, editNote, toggleNote } from "./auxiliary/Action"; 
import Header from "./components/Header";
import Footer from "./components/Footer";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage"; 
import HomePage from "./pages/HomePage";

//we need to remove the hard code here
export const initialState = {
  user: null,
  posts: [],
  currentView: 'homepage',
  //Store registered users in the database
  mockDB: []
};


export default function App() {

  //1. we need to use useResource to set up our url and posts
  const [postResponse, getPosts] = useResource(() => ({
    url: "/posts",
    method: "get",
  })); 

  //Requesting posts during component loading
  //Empty dependency array ensures that it only runs 
  //when the component is mounted
  useEffect(() => {
    getPosts();
  }, []);

  //Update status when the response to the request arrives
  useEffect(() => {
    if (postResponse && postResponse.data) {
      dispatch({ type: "FETCH_POSTS", posts: postResponse.data.reverse() });
    }
  }, [postResponse]);



  const mainReducer = (state, action) => {
    const updatedUserAndView = userAndViewReducer(state, action);
    return {
      ...updatedUserAndView,
      posts: notesReducer(state.posts, action),
      mockDB: mockDBReducer(state.mockDB, action),
    };
  };

  const [state, dispatch] = useReducer(mainReducer, initialState);

  const { user, posts: notes, currentView } = state;

  //Modal Window Status
  const [showModal, setShowModal] = useState(false);

  //Use useEffect to check for changes in user status
  useEffect(() => {
    if (user) {
      setShowModal(true);
    }
  }, [user]);


  return(
    <div>
      <StateContext.Provider value={{ state, dispatch }}>

        <Header />
        {currentView === 'login' && <LoginPage />}
        {currentView === 'register' && <RegisterPage />}
        {currentView === 'homepage' && <HomePage 
          notes={notes} 
          addNote={(note) => dispatch(addNote(note))}
          deleteNote={(id) => dispatch(deleteNote(id))}
          editNote={(attributes) => dispatch(editNote(attributes))}
          toggleNote={(id) => dispatch(toggleNote(id))}
        />}
        <Footer />

        {/* New Modal Window */}
        {showModal && (
          <div className="modal">
            <div className="modal-content">
              Welcome back, {user.name}!
              <button onClick={() => setShowModal(false)}>Close</button>
            </div>
          </div>
        )}

      </StateContext.Provider>
    </div>
  )
}


  /**

      dispatch(loginUser(user.name)); 这个代码在loginpage中，注意假设这个函数被调用
      首先，从usecontext全局那里拿到state dispatch，然后这个dispatch(setView('homepage'))来源于reducer.jsx
      当调用的时候，会触及这个const [state, dispatch] = useReducer(mainReducer, initialState);也就是会来源于mainreducer
      传入当前的状态和刚才 dispatch 的 action。
      子 reducer 被调用：
      mainReducer 会分别调用 userAndViewReducer、notesReducer 和 mockDBReducer，每个子 reducer 都处理它们各自负责的状态部分。

      状态被更新：
      userAndViewReducer 可能会返回更新了 user 或 currentView 的状态部分。
      notesReducer 根据 action 和 state.posts 返回更新后的帖子列表。
      mockDBReducer 根据 action 和 state.mockDB 返回更新后的模拟数据库状态。

      新的状态对象被组合：
      mainReducer 会将所有子 reducer 返回的结果合并为一个新的状态对象，并将其返回给 React。React 随后会使用这个新状态对象来重新渲染 UI。
   */
