import React, { useState, useContext, useEffect } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "../auxiliary/Context"; 

function CreateArea(props) {

  const { state } = useContext(StateContext);
  const { user } = state;

  const [note, setNote] = useState({
    title: "",
    content: ""
  });

  const [post, createPost] = useResource(({ title, content, createdDate, isCompleted, completedDate, userEmail }) => ({
    url: "/posts",
    method: "post",
    data: { title, content, createdDate, isCompleted, completedDate, userEmail },
  }));

  //重点代码：我们检查 post.isLoading 是否为 false，这样我们就知道请求已经完成，并且有数据可用。
  //这样可以防止在数据还没有准备好的时候就调用 props.onAdd
  //同时也防止重复渲染，因为只要post发生变化就会进来props.add，但我们保证请求完成就不会再进入这个if块了
  useEffect(() => {
    if (post && post.data && post.isLoading === false) {
      console.log(post.data);
      props.onAdd(post.data);
      setNote({
        title: "",
        content: ""
      });
    }
  }, [post]);

  function handleChange(event) {
    const { name, value } = event.target;

    setNote(prevNote => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  async function submitNote(event) {
    event.preventDefault(); 

    const newNote = {
      title: note.title,
      content: note.content,
      createdDate: new Date().toLocaleString(),
      isCompleted: false,
      completedDate: null,
      userEmail: user.name
    };

   createPost(newNote); 

  }

  return (
    <div>
      <form>
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
        />
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows="3"
        />
        <button onClick={submitNote} disabled={!note.title}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
