import React, { useContext } from "react";
import { StateContext } from "../auxiliary/Reducer"; 
import { setView, logoutUser} from "../auxilary/Action"; 

function Header() {
  const { state: user, dispatch } = useContext(StateContext);

  const handleLogout = () => {
    window.alert("Log Out Successfully!");
    dispatch(logoutUser());
    dispatch(setView('login'));
  };

  return (
    <header>
      {user ? (
        <>
          <h1>Author: {user}</h1>
          <button onClick={handleLogout}>Log Out</button>
        </>
      ) : (
        <h1>To-Do App</h1>
      )}
    </header>
  );
}

export default Header;
